import React  from 'react';
import Canvas from "./Canvas"
import './App.css';

const dic = {
  Rebecca:"red",
  Jeff:"Blue"
}

class App extends React.Component{
    constructor(props){
        super(props)
        this.state={
            text:"",
            top: 0,
            left: 0
        }


        this.handleChange = this.handleChange.bind(this)
    }

    /*
    state = {
      colors: [
        {key:'red' color:'red'}
        {key:'yellow' color:'yellow'}
        {key:'blue' color:'blue'}
        {key:'Rebecca' color:''}
        {key:'Gayatri' color:''}
        {key:'Gryffindor' color:''}
        {key:'' color:''}
        {key:'' color:''}
      ]
    }
    */



    handleChange(e){
        var name = e.target.value
        this.setState({text:name})
        var top = Math.random(0,1) * 600
        var left = Math.random(0,1) * 500
        var width= Math.random(0,1) * 600;
        var height= Math.random(0,1) * 1000;
        this.setState({top:top,left:left,width: width, height:height})
    }

   renderBox(text){
     var style = {
      position:"absolute",
      width: `${this.state.width}px`,
      height: `${this.state.height}px`,
      top:`${this.state.top}px`,
      left:`${this.state.left}px`
     }

     //actual colors
     if(text.search(new RegExp('red', 'i')) != -1){
       style.background = `rgb(255,0,0,0.5)`;
     }
     if(text.search(new RegExp('yellow', 'i')) != -1){
       style.background = `rgb(255,0,0,0.5)`;
     }
     if(text.search(new RegExp('blue', 'i')) != -1){
       style.background = `rgb(255,0,0,0.5)`;
     }
     if(text.search(new RegExp('green', 'i')) != -1){
       style.background = `rgb(255,0,0,0.5)`;
     }
     if(text.search(new RegExp('purple', 'i')) != -1){
       style.background = `rgb(255,0,0,0.5)`;
     }

     /*
     //friends & people
     if(text.includes('Rebecca')){
       style.background =
     }
     if(text.includes('Gayatri')){
       style.background =
     }
     if(text.includes('Tajamara')){
       style.background =
     }
     if(text.includes('Blake')){
       style.background =
     }

     //heavy connotations
     if(text.includes('death')){
       style.background =
     }
     if(text.includes('life')){
       style.background =
     }
     if(text.includes('')){
       style.background =
     }
     if(text.includes('')){
       style.background =
     }
    }
    */
     return <Canvas style={style} />
   }



    /*
    function Canvas(props) {
      return (
        <div className="canvas" name="txt" className="canvas-input" value={this.state.text}
        onChange={this.handleChange} placeHolder='input color!' />
          <style color=props.color></style>
        </div>
      );
    }
    */



    render(){
        return <div className='canvas'>

        <input type='text'name='txt' className='canvas-input' value={this.state.text}
        onChange={this.handleChange} placeholder='input color!' />

          {this.renderBox(this.state.text)}

          </div>
          /*
          {this.state.text.includes("red") &&
          <div className='canvas_red' style={{position:"absolute",top:`${this.state.top}px`,left:`${this.state.left}px`}}></div>
          }

           {this.state.text.includes("blue") &&
          <div className='canvas__blue' style={{position:"absolute",top:`${this.state.top}px`,left:`${this.state.left}px`}}></div>
          }

          {this.state.text.includes("purple") &&
          <div className='canvas__purple' style={{position:"absolute",top:`${this.state.top}px`,left:`${this.state.left}px`}}></div>
        }*/

          /*
          {this.state.text.includes("yellow") &&
          <div className='canvas' style={{position:"absolute",top:`${this.state.top}px`,left:`${this.state.left}px`} color:``}></div>
          }
          */
    }
}



export default App;
