import React  from 'react';
import Canvas from "./Canvas"
import './App.css';

const dic = {
  Rebecca:"red",
  Jeff:"Blue"
}

class App extends React.Component{
    constructor(props){
        super(props)
        this.state={
            text:"",
            top: 0,
            left: 0
        }


        this.handleChange = this.handleChange.bind(this)
    }

    /*
    state = {
      colors: [
        {key:'red' color:'red'}
        {key:'yellow' color:'yellow'}
        {key:'blue' color:'blue'}
        {key:'Rebecca' color:''}
        {key:'Gayatri' color:''}
        {key:'Gryffindor' color:''}
        {key:'' color:''}
        {key:'' color:''}
      ]
    }
    */



    handleChange(e){
        var name = e.target.value


        this.setState({text:name})


        var top = Math.random(0,1) * 800
        var left = Math.random(0,1) * 500
        this.setState({top:top,left:left})
    }

   renderBox(text){
     if(text.includes("red")){
       alert("red")
       var style =  {
            position:"absolute",

            top:`${this.state.top}px`,
            left:`${this.state.left}px`}
       return <Canvas style={style} />
     }

   }



    /*
    function Canvas(props) {
      return (
        <div className="canvas" name="txt" className="canvas-input" value={this.state.text}
        onChange={this.handleChange} placeHolder='input color!' />
          <style color=props.color></style>
        </div>
      );
    }
    */



    render(){
        return <div className='canvas'>

        <input type='text'name='txt' className='canvas-input' value={this.state.text}
        onChange={this.handleChange} placeholder='input color!' />

          {this.renderBox(this.state.text)}

          </div>
          /*
          {this.state.text.includes("red") &&
          <div className='canvas_red' style={{position:"absolute",top:`${this.state.top}px`,left:`${this.state.left}px`}}></div>
          }

           {this.state.text.includes("blue") &&
          <div className='canvas__blue' style={{position:"absolute",top:`${this.state.top}px`,left:`${this.state.left}px`}}></div>
          }

          {this.state.text.includes("purple") &&
          <div className='canvas__purple' style={{position:"absolute",top:`${this.state.top}px`,left:`${this.state.left}px`}}></div>
        }*/

          /*
          {this.state.text.includes("yellow") &&
          <div className='canvas' style={{position:"absolute",top:`${this.state.top}px`,left:`${this.state.left}px`} color:``}></div>
          }
          */
    }
}



export default App;
