import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      text: '',
      words: [],
      colorLib: [
        //{word: '', color:`rgb()`}
        {word: 'red', color:`rgb(255, 0, 0, 1)`},
        {word: 'yellow', color:`rgb(255, 255, 0, 1)`},
        {word: 'blue', color:`rgb(0, 0, 255, 1)`},
        {word: 'green', color:`rgb(0, 255, 0, 1)`},
        {word: 'purple', color:`rgb(255, 0, 255, 1)`}
      ]
    };
    this.textChangeHandler = this.textChangeHandler.bind(this);
  }

  textChangeHandler= (e) => {
    var text = e.target.value;
    this.setState({text: text});
    //reconstruct the libary of words
    var words = [];
    var word = '';
    for (var i = 0; i < text.length; i++) {
      var ch = text.charAt(i);
      var nextCh = text.charAt(i + 1);
      if (ch !== ' ') {
        word += ch;
      } else if (ch === ' ' && nextCh !== ' ') {
        words.push(word);
        word = '';
      }
    }
    //bug: continuous spaces produce a period for some reason
    this.setState({words: words});
    //find the new corresponding colors

  }

  /*
  findColors = (word) => {
    displayedColors = [];
  }
  */

  render(){
    let colors = null;
    colors = (
      <div>
        {this.state.displayedColors.map((color) => {
          return <Canvas color = {color}/>
        })}
      </div>
    );

    return (
      <div>
        <div><label className='canvas-label'>Start typing and see what happens...</label></div>
        <textarea type='text'name='txt' className='canvas-input' value={this.state.text}
        onChange={this.textChangeHandler} placeholder='' rows='5'/>
        <div><label className='word-count'>Word Count: {this.state.words.length}</label></div>
        {colors}
      </div>
    );
  }
}

/*{this.state.colors.map((color,key) => {
  return <div key={key} style={color}></div>
})}
*/

export default App;
