import React  from 'react';
import './App.css';


class App extends React.Component{
    constructor(props){
        super(props)
        this.state={
            text:"",
            top:300,
            left:300
        }


        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(e){
        this.setState({text:e.target.value})
    }

    randomShow(){
      var top = Math.random(0,1) * 1000
        var left = Math.random(0,1) * 1000
      this.setState({top:top,left:left})
    }

    /*
    function Canvas(props) {
      return (
        <div className="canvas" name="txt" className="canvas-input" value={this.state.text}
        onChange={this.handleChange} placeHolder='input color!' />
          <style color=props.color></style>
        </div>
      );
    }
    */



    render(){
        return <div className='canvas'>

        <input type='text'name='txt' className='canvas-input' value={this.state.text}
        onChange={this.handleChange} placeholder='input color!' />
        {this.state.text.includes("red") &&
        <div className='canvas__red' style={{position:"absolute",top:`${this.state.top}px`,left:`${this.state.left}px`}}></div>
        }

         {this.state.text.includes("blue") &&
        <div className='canvas__blue'></div>
        }

        {this.state.text.includes("purple") &&
        <div className='canvas__purple'></div>
        }
        </div>





    }
}



export default App;
