import React  from 'react';
import './App.css';

class App extends React.Component{
    constructor(props){
        super(props)
        this.state={
            text:"",
            top: 0,
            left: 0
        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(e){
        var name = e.target.value
        this.setState({text:name})
        var top = Math.random(0,1) * 600
        var left = Math.random(0,1) * 500
        var width= Math.random(0,1) * 600;
        var height= Math.random(0,1) * 1000;
        this.setState({top:top,left:left,width: width, height:height})
        //need to make sure that everything stays on the screen without scrolling down
    }

   renderBox(text){
     var style = {
      position:"absolute",
      width: `${this.state.width}px`,
      height: `${this.state.height}px`,
      top:`${this.state.top}px`,
      left:`${this.state.left}px`
     }

     //actual colors
     if(text.search(new RegExp('red', 'i')) != -1){
       style.background = `rgb(255,0,0,0.5)`;
     }
     if(text.search(new RegExp('yellow', 'i')) != -1){
       style.background = `rgb(255,255,0,0.5)`;
     }
     if(text.search(new RegExp('blue', 'i')) != -1){
       style.background = `rgb(0,0,255,0.5)`;
     }
     if(text.search(new RegExp('green', 'i')) != -1){
       style.background = `rgb(0,255,0,0.5)`;
     }
     if(text.search(new RegExp('purple', 'i')) != -1){
       style.background = `rgb(255,0,255,0.5)`;
     }

     //friends & special people

     //Hogwarts

     //special words with heavy connotation


     return <div style={style}></div>
   }

    render(){
        //CHANGE: ONLY RENDER BOX WHEN THERE IS A NEW WORD (WITH SPACE)
        return <div className='canvas'>
            <div><label className='canvas-label'>Start typing and see what happens...</label></div>
            <textarea type='text'name='txt' className='canvas-input' value={this.state.text}
            onChange={this.handleChange({txt})} placeholder='' />
              {this.renderBox(this.state.text)}
        </div>
    }
}



export default App;
