import React  from 'react';
import './App.css';

class App extends React.Component{
    constructor(props){
        super(props)
        this.state={
            text:'',
            word:'',
            display: false,
            colors: [],
            top: 0,
            left: 0
        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange = (e) => {
        var name = e.target.value
        this.setState({text:name})
        var ch = name.charAt(name.length-1)
        var w = this.state.word
        if (ch == ' ') {
          var style = {
           position:"absolute",
           width: `${this.state.width}px`,
           height: `${this.state.height}px`,
           top:`${this.state.top}px`,
           left:`${this.state.left}px`
          }
          var top = Math.random(0,1) * 600;
          var left = Math.random(0,1) * 500;
          var width= Math.random(0,1) * 600;
          var height= Math.random(0,1) * 1000;

          //actual colors
          if(word.search(new RegExp('red', 'i')) != -1){
            style.background = `rgb(255,0,0,0.5)`;
          }
          if(word.search(new RegExp('yellow', 'i')) != -1){
            style.background = `rgb(255,255,0,0.5)`;
          }
          if(word.search(new RegExp('blue', 'i')) != -1){
            style.background = `rgb(0,0,255,0.5)`;
          }
          if(word.search(new RegExp('green', 'i')) != -1){
            style.background = `rgb(0,255,0,0.5)`;
          }
          if(word.search(new RegExp('purple', 'i')) != -1){
            style.background = `rgb(255,0,255,0.5)`;
          }

          //special words with heavy connotation
          if(word.search(new RegExp('happiness', 'i')) != -1){
            style.background = `rgb(255,0,255,0.5)`;
          }
          if(word.search(new RegExp('death', 'i')) != -1){
            style.background = `rgb(255,0,255,0.5)`;
          }
          if(word.search(new RegExp('sex', 'i')) != -1){
            style.background = `rgb(255,0,255,0.5)`;
          }
          if(word.search(new RegExp('ending', 'i')) != -1){
            style.background = `rgb(255,0,255,0.5)`;
          }
          if(word.search(new RegExp('rjed', 'i')) != -1){
            style.background = `rgb(255,0,255,0.5)`;
          }
          if(word.search(new RegExp('purple', 'i')) != -1){
            style.background = `rgb(255,0,255,0.5)`;
          }

          //Hogwarts
          if(word.search(new RegExp('gryffindor', 'i')) != -1){
            style.background = `rgb(255,0,255,0.5)`;
          }
          if(word.search(new RegExp('hufflepuff', 'i')) != -1){
            style.background = `rgb(255,0,255,0.5)`;
          }
          if(word.search(new RegExp('slytherin', 'i')) != -1){
            style.background = `rgb(255,0,255,0.5)`;
          }
          if(word.search(new RegExp('ravenclaw', 'i')) != -1){
            style.background = `rgb(255,0,255,0.5)`;
          }

          this.setState({top:top,left:left,width: width, height:height, word:''})

          this.setState((prevState)=>{
            return {
              colors: preState.colors.concat(style)
            }
          })
          this.setState({display:true})
        } else {
          w += ch
          this.setState({word: w});
        }

        //need to make sure that everything stays on the screen without scrolling down
    }
    /*
   renderBox(word){
     var style = {
      position:"absolute",
      width: `${this.state.width}px`,
      height: `${this.state.height}px`,
      top:`${this.state.top}px`,
      left:`${this.state.left}px`
     }


     //friends & special people


     return <div style={style}></div>
   }
   */

    render(){
      return <div className='canvas'>
        {this.state.colors.map((color,key) => {
          return <div key={key} style={color}></div>
        })}

        <div><label className='canvas-label'>Start typing and see what happens...</label></div>
        <textarea type='text'name='txt' className='canvas-input' value={this.state.text}
        onChange={this.handleChange} placeholder='' rows='5'/>
        <div><label className='word-count'>Character Count: {this.state.text.length}</label></div>
      </div>
    }
}



export default App;
